    try {
        const data = await tiendas.find()
    if(data.length <= 0) create()
    else{ console.log('ubicaciones ya creadas')}
    } catch (error) {
        console.log(error);
        console.log('hubo un error en la busqueda de ubicaciones');
    }
}
async function create() {
  try {
    const valencia ={
        nombre: 'valencia',
    
    }
    const barquisimeto ={
        nombre: 'barquisimeto',
    
    }
    const vigia ={
        nombre: 'el vigia',
    
    }
    await new tiendas(valencia).save()
    await new tiendas(barquisimeto).save()
    await new tiendas(vigia).save()
  } catch (error) {
      console.log('hubo un error en la creacion de ubicaciones');
  }
}

